import React, { createContext, useContext, useState, useEffect } from 'react';
import { SimpleAuth } from './simple-auth';

const AuthContext = createContext();
const authService = new SimpleAuth();

/**
 * Auth Context Provider
 * Manages authentication state for the entire app
 * Simple session-based auth, no complex token management
 */
export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check for existing session
    checkSession();
    setLoading(false);
  }, []);

  const checkSession = () => {
    // For demo, check localStorage
    const authData = localStorage.getItem('haven_auth');
    if (authData) {
      try {
        const { user } = JSON.parse(authData);
        setUser(user);
        setIsAuthenticated(true);
      } catch (e) {
        localStorage.removeItem('haven_auth');
      }
    }
  };

  const login = async (email, password) => {
    try {
      const result = await authService.login(email, password);

      // Store session
      localStorage.setItem('haven-session', result.sessionId);

      // Update state
      setUser(result.user);
      setIsAuthenticated(true);

      return result;
    } catch (error) {
      throw error;
    }
  };

  const register = async (email, password, name) => {
    try {
      const result = await authService.register(email, password, name);
      return result;
    } catch (error) {
      throw error;
    }
  };

  const logout = () => {
    const sessionId = localStorage.getItem('haven-session');
    if (sessionId) {
      authService.logout(sessionId);
    }

    localStorage.removeItem('haven-session');
    setUser(null);
    setIsAuthenticated(false);

    // Redirect to login
    window.location.href = '/login';
  };

  const value = {
    user,
    isAuthenticated,
    loading,
    login,
    register,
    logout
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
}
